// functional component, classs component
// passing prop, default prop, prop check
// conditional rendering( ternary operator, if, ampercend)
// list and keys
// useState, useReducer, useEffect, useRef, useContext, useCallback, useMemo
// useCallback hook performence optimization technique
// controlled and uncontrolled component
// toggle
// api call axios and useeffect (ajax call)
// lazy loading
// create form and show form data
// custom hook (api calling)
// Todo list

import React, { useCallback, useState } from 'react';

function Parent() {
  console.log("Parent rendered");

  const [count, setCount] = useState(0);
  const [trigger, setTrigger] = useState(0);

  // handleButtonClick only changes when `trigger` changes
  const handleButtonClick = useCallback(() => {
    alert(`Triggered from Child! Dependency value: ${trigger}`);
  }, [trigger]);

  return (
    <div>
      <h1>Parent Component</h1>
      <p>Count: {count}</p>
      <p>Trigger Dependency: {trigger}</p>

      <button onClick={() => setCount(prev => prev + 1)}>
        Update Count (No effect on Child)
      </button>

      <button onClick={() => setTrigger(prev => prev + 1)}>
        Update Trigger (Affects Child)
      </button>

      {/* Passing the memoized function as a prop */}
      <Child onButtonClick={handleButtonClick} />
    </div>
  );
}

// React.memo prevents re-render unless props change
const Child = React.memo(({ onButtonClick }) => {
  console.log("Child rendered");

  return (
    <div>
      <h2>Child Component</h2>
      <button onClick={onButtonClick}>Click Me</button>
    </div>
  );
});

export default Parent;
