import React, { useState, useEffect, useRef } from "react";

const FC = () => {
    let [count, setCount] = useState(0)
    let persistValue = useRef(0)

    useEffect(() => {
        persistValue.current = count
    }, [count])

    return (
        <>
            <h1>Count {count}</h1>
            <h2>These give pre</h2>
            <button onClick={() => { setCount(++count) }}>INC Count</button>
        </>
    )
}

export default FC;
