import React from "react";

const TernaryOperator = () => {
    const user = {
        name: 'Mansoor',
        isLogged: false
    }
    let message;
    if(user.isLogged){
        message = <h1> Hi {user.name} you logged in ...</h1>
    }else{
        message = <h1> Hi {user.name} please login first...</h1>
    }
    return(    
        <>
        {message}
        </>
    )
}

export default TernaryOperator;
