import { useEffect, useState } from "react";

// export default FC;



// hooks(useState, useeffect, usereducer,useref,usecontext,usecallback,usememo)

const FC = () =>{
    let [counter, setCounter] = useState(0);
    let [effect, setEffect] = useState(0)
    useEffect(() => {
        console.log('These gets logged based on the dependency')
    },[e])
    return(
        <>
        <h1>The current value of the counter is:{counter}</h1>
        <button onClick={()=>{setCounter(++counter)}}>INC</button>
        <button onClick={()=>{setEffect(++effect)}}>RUN Effect</button>
        </>
    )
}

export default FC;
