//hooks(useState, useeffect, usereducer,useref,usecontext,usecallback,usememo)

import React from "react";

class FC extends React.Component{
     state = {
        count : 0
    }
    render(){
        return(
            <>
            <h1>The current value of the state is : {this.state.count}</h1>
            <button onClick={()=>{
                this.setState({
                    count: this.state.count + 1
                })
            }}>INC</button>
            <button onClick={()=>{
                this.setState({
                    count: this.state.count - 1
                })
            }}>DEC</button>
            <button onClick={()=>{
                this.setState({
                    count: 0
                })
            }}>RESET</button>
            </>
        )
    }
}
export default FC;
