import React, { useState, lazy, Suspense } from "react";

const LazyComponent = lazy(()=>import('./ControlledComponent'))

const FC = () => {
    let [show, isShow] = useState(false)
    return (
        <>
            <h1>Lazy Component</h1>
            <button onClick={()=>{isShow(!show)}}>Load Compoent</button>
            <Suspense fallback={<p>Loading.....</p>}>
            {show && <LazyComponent />}
            </Suspense>
        </>
    )
}

export default FC
