import React, {useState, Suspense, lazy} from "react";

const FC = () => {
    let [show, isShow] = useState(false)
    return(
        <>
        <button onClick={()=>{isShow(!show)}}>Load component</button>
        {show && <Suspense fallback=<h1>Loading</h1>>
            </Suspense>}
        </>
    )
}

export default FC;
